import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as action from './actions';

const AddTodo = ({ dispatch }) => {
  let input

  return (
    <div>
      <form onSubmit={e => {
        e.preventDefault()
        if (!input.value.trim()) {
          return
        }
        dispatch(action.setPetName(input.value))
        input.value = ''
      }}>
        <button type="submit">
          Add Todo
        </button>
      </form>
    </div>
  )
}


class App extends Component {

  render() {

    return (
      <div className="App">
        main



      </div>
    );
  }
}

export default connect()(App);
