import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as action from './actions';

class App extends Component {

  render() {
    let input 
    return (
      <div className="App">

        <div>
          <form onSubmit={e => {
            e.preventDefault()
            if (!input.value.trim()) {
              return
            }

            input.value = ''
          }}>
            <input ref={node => {
              input = node
            }} />
            <button type="submit">
              Add Todo
            </button>
          </form>
        </div>



      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    petName: state.constants.petName
  }
}

export default connect()(App);
