const initialState = {
  constants: {
    petName: 'asdf',
    userName: 'ghjk',
  },
  pet: {
    variables: {
      health: 100,
      hunger: 100,
      mood: 'neutral',
      happiness: 60,
      exp: 0
    }
  },
  user: {
    money: 0,
    items: [
      {
        name: 'medicine',
        targets: ['pet.variables.health', 10]  
      },
      {
        name: 'grooming kit',
        targets: ['pet.variables.happiness', 20]  
      },
    ]
  }
}

export default initialState
