    return (
      <div className="App">
      <h2>Stats</h2>
      <p>Pet name: {this.props.state.constants.petName}</p>


      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    state
  }
}

export default connect(mapStateToProps)(App);
