import React, {Component} from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { playCatch, goToSleep, eatFood } from '../actions'
import * as utils from '../functions';

class Commands extends Component{
  render() {
    const props = this.props.state
    console.log('Commands', this.props)
    utils.healthButton(120)

    return(
      <React.Fragment> 
        <h3>Actions</h3>
        <button onClick={this.props.eatFood} disabled={healthButton(props.hunger)}>Eat Food</button>
        <button onClick={this.props.goToSleep} disabled={props.health >= 100}>Sleep</button>
        <button onClick={this.props.playCatch} disabled={props.hunger <= 10 || props.health <= 5}>Play Catch</button>
      </React.Fragment>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ eatFood, goToSleep, playCatch }, dispatch);
}

const mapStateToProps = (state) => {
  return {
    state: state.stats
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Commands)
