import React, {Component} from 'react';
import { connect } from 'react-redux';
import { playCatch, goToSleep, eatFood } from '../actions'

class Commands extends Component{
  render() {
    // const { dispatch } = this.props
    const { dispatch } = this.props
    console.log('Commands', this.props)

    // const { playCatch, goToSleep, eatFood } = this.props;
    return(
      <React.Fragment> 
        <h3>Actions</h3>
        <button onClick={this.eatFood.bind(this)}>Eat Food</button>
        <button onClick={goToSleep}>Sleep</button>
        <button onClick={playCatch}>Play Catch</button>
      </React.Fragment>
    )
  }
}

const mapDispatchToProps = ({dispatch}) => {
  return {
    eatFood: () => dispatch(eatFood()),
    goToSleep: () => dispatch(goToSleep()),
    playCatch: () => dispatch(playCatch())
  }
}

export default connect(null, mapDispatchToProps)(Commands)
