import React, { Component } from 'react'
import { connect } from 'react-redux';
import {  } from '../actions'

class Stats extends Component {
  render() {
    const props = this.props.state

    console.log('Stats this.props', this.props.state)
    return(
      <div>
        <h3>Stats</h3>
        <p>health: {props.health}</p>
        <div className="bar" style={{width: `${props.health}px`, height: '5px', backgroundColor: 'powderblue'}}/>
        <p>hunger: {props.hunger}</p>
        <p>mood: {props.mood}</p>
        <p>happiness: {props.happiness}</p>
        <p>experience: {props.exp}</p>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    state: state.stats
  }
}

export default connect(mapStateToProps)(Stats)
